  `isdir` tinyint(1) NOT NULL DEFAULT '0' COMMENT '是否目录',
  `display` tinyint(1) NOT NULL DEFAULT '1' COMMENT '0=>不显示,1=>显示',
  `isshow` int(1) NOT NULL DEFAULT '1' COMMENT '1角色授权是否显示',
