  `reg_range` tinyint(4) NOT NULL COMMENT '玩家区间（天数）',
  `date` date NOT NULL COMMENT '统计时间',
  `load_count` int(11) NOT NULL DEFAULT '0' COMMENT '在reg_range内注册，在date当天登录的',
  `pay_amount` double(11,2) NOT NULL DEFAULT '0.00' COMMENT '充值总额',
  `pay_count` int(11) NOT NULL DEFAULT '0' COMMENT '付费人数',
  `pay_range_1` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间1-10',
  `pay_range_2` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间11-20',
  `pay_range_3` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间21-50',
  `pay_range_4` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间51-100',
  `pay_range_5` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间101-200',
  `pay_range_6` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间201-300',
  `pay_range_7` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间301-500',
  `pay_range_8` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间501-1000',
  `pay_range_9` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间1001-2000',
  `pay_range_10` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间2001-5000',
  `pay_range_11` varchar(64) NOT NULL DEFAULT '' COMMENT '充值区间5001以上',
  PRIMARY KEY (`id`),
  KEY `date` (`date`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8 ROW_FORMAT=DYNAMIC COMMENT='充值区间查看';

CREATE TABLE `st_payment` (
  `id` bigint(11) unsigned NOT NULL AUTO_INCREMENT,
  `pfrom_id` smallint(50) NOT NULL DEFAULT '1',
  `server_id` smallint(5) unsigned NOT NULL DEFAULT '0' COMMENT '区服id',
  `pay_count` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '充值人数',
  `pay_times` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '充值次数',
  `pay_amount` double(10,2) unsigned NOT NULL DEFAULT '0.00' COMMENT '充值金额',
  `pay_date` date NOT NULL DEFAULT '0000-00-00' COMMENT '充值日期',
  `pay_hour` tinyint(3) NOT NULL DEFAULT '0' COMMENT '充值小时',
  `channel` int(11) NOT NULL DEFAULT '0' COMMENT '渠道标识',
  PRIMARY KEY (`id`),
  KEY `pfrom_id_server_id` (`server_id`),
  KEY `pay_date` (`pay_date`)
) ENGINE=InnoDB AUTO_INCREMENT=1 DEFAULT CHARSET=utf8;

